import React, { Component } from 'react';
import { Global } from '@emotion/core'
import styled from '@emotion/styled'
import { ThemeProvider } from 'emotion-theming'
import theme from '../../theme.js'
import {
  Flex,
  Box,
  Heading,
  Text,
  Button,
  Link as A,
} from 'rebass'

class DemoIndex extends Component {

  render() {
    return (
      <ThemeProvider theme={theme}>
        <Global
          styles={{
            'body, html': {
              padding: 0,
              margin: 0
            }
          }}
        />
        <Flex
          bg="black"
          color="white"
          flexDirection="column"
          alignItems="center"
          justifyContent="center"
          height="100%"
          minHeight="100vh"
          className="sweetmenu__demo">
          <Box className="sweetmenu__demo--container">
            <Heading
              fontFamily="body"
              fontWeight="bold"
              fontSize={[6,6,6,8]}
              className="sweetmenu__demo--title">
              Welcome
            </Heading>
            <Button variant="inverted">
              Get access
            </Button>
          </Box>
        </Flex>
      </ThemeProvider>
    );
  }

}

export default DemoIndex;
